import React, { useState } from 'react';

const EditAddressModal = ({ isOpen, onClose }) => {
    if (!isOpen) return null;

    const modalStyle = {
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        backgroundColor: '#fff',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        width: '400px',
        zIndex: 1000,
    };

    const overlayStyle = {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.3)',
        zIndex: 999,
    };

    const inputStyle = {
        width: '100%',
        padding: '10px',
        margin: '10px 0',
        border: '1px solid #ccc',
        borderRadius: '4px',
    };

    const buttonStyle = {
        padding: '10px 15px',
        border: 'none',
        borderRadius: '4px',
        fontWeight: 'bold',
        backgroundColor: '#000',
        color: '#fff',
        cursor: 'pointer',
        width: '100%',
    };

    const labelButtonStyle = (active) => ({
        padding: '10px 20px',
        borderRadius: '4px',
        border: `2px solid ${active ? '#000' : '#ccc'}`,
        backgroundColor: active ? '#000' : '#fff',
        color: active ? '#fff' : '#000',
        cursor: 'pointer',
        marginRight: '10px',
    });

    const [selectedLabel, setSelectedLabel] = useState('Home');

    return (
        <>
            <div style={overlayStyle} onClick={onClose}></div>
            <div style={modalStyle}>
                <h2>Edit Address</h2>
                <button onClick={onClose} style={{ float: 'right', fontSize: '1.2rem', cursor: 'pointer' }}>Ã—</button>
                <form>
                    <input style={inputStyle} type="text" placeholder="Name" />
                    <input style={inputStyle} type="text" placeholder="Phone Number" />
                    <input style={inputStyle} type="text" placeholder="Region, Province, City, Barangay" />
                    <input style={inputStyle} type="text" placeholder="Postal Code" />
                    <input style={inputStyle} type="text" placeholder="Street Name, Building, House No" />
                    <div style={{ height: '150px', border: '1px solid #ccc', borderRadius: '4px', margin: '10px 0', textAlign: 'center', lineHeight: '150px' }}>
                        Google Map
                    </div>
                    <div style={{ margin: '10px 0' }}>
                        <button
                            style={labelButtonStyle(selectedLabel === 'Home')}
                            onClick={(e) => {
                                e.preventDefault();
                                setSelectedLabel('Home');
                            }}
                        >
                            Home
                        </button>
                        <button
                            style={labelButtonStyle(selectedLabel === 'Work')}
                            onClick={(e) => {
                                e.preventDefault();
                                setSelectedLabel('Work');
                            }}
                        >
                            Work
                        </button>
                    </div>
                    <button style={buttonStyle}>SAVED</button>
                </form>
            </div>
        </>
    );
};

export default EditAddressModal;
