import React, { useState, useEffect } from 'react';
import { FaChevronUp, FaChevronDown } from 'react-icons/fa'; // Importing the icons
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa'; // Import the icons
import styles from '../styles/home.module.css';
import '../styles/global.css';
import HomeHeader from '../components/home-header';
import babiesAndToddlersImg1 from "../assets/books/Babies and Toddlers/Bedtime Stories.png";
import babiesAndToddlersImg2 from "../assets/books/Babies and Toddlers/Religious Theme.jpg";
import emergentReadersImg1 from "../assets/books/Emergent Readers/Ang Maligayang Kapaskuhan.png";
import emergentReadersImg2 from "../assets/books/Emergent Readers/Environmental Responsibility.png";
import emergentReadersImg3 from "../assets/books/Emergent Readers/Feelings and Emotions.png";
import emergentReadersImg4 from "../assets/books/Emergent Readers/Historical Event.png";
import independentReadersImg1 from "../assets/books/Independent Readers/Family Life.png";
import independentReadersImg2 from "../assets/books/Independent Readers/Fiction.png";
import avidReadersImg1 from "../assets/books/Avid Readers/The Skaters.png";
import avidReadersImg2 from "../assets/books/Avid Readers/Wicked Princess.png";

const Home = () => {
  const [priceOpen, setPriceOpen] = useState(false);
  const [genreOpen, setGenreOpen] = useState(false);
  const [typeOpen, setTypeOpen] = useState(false);
  const [formatOpen, setFormatOpen] = useState(false);
  const [languageOpen, setLanguageOpen] = useState(false);
  const [prelovedOpen, setPrelovedOpen] = useState(false);

  const [books, setBooks] = useState([]); // State to store the books data
  const [loading, setLoading] = useState(true); // State to handle loading state

  useEffect(() => {
    // Fetch books from the getBooks.php endpoint
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:8000/getBook.php'); // Replace with your actual server URL
        if (!response.ok) {
          throw new Error('Failed to fetch books');
        }
        const data = await response.json();
        if (data.success) {
          setBooks(data.books); // Store books in state
        } else {
          console.error('Failed to fetch books:', data.message);
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchBooks();
  }, []);

  const [sortOption, setSortOption] = useState("Price: Low to High");
  const [sortOpen, setSortOpen] = useState(false); // Define the sortOpen state

  const [readerOpen, setReaderOpen] = useState(false);
  const [selectedReaders, setSelectedReaders] = useState({
    'Babies and Toddlers': false,
    'Emergent Readers': false,
    'Independent Readers': false,
    'Avid Readers': false,
  });

  // Handle dropdown item click
  const handleSortOptionChange = (option) => {
    setSortOption(option);
    setSortOpen(false); // Close dropdown after selecting an option
  };

  const handleCheckboxChange = (category) => {
    setSelectedReaders(prevState => ({
      ...prevState,
      [category]: !prevState[category],
    }));
  };

  // Dynamically select content based on the selected checkboxes
  const getFilteredImages = () => {
    let filteredImages = [];

    if (selectedReaders['Babies and Toddlers']) {
      filteredImages.push(babiesAndToddlersImg1, babiesAndToddlersImg2);
    }
    if (selectedReaders['Emergent Readers']) {
      filteredImages.push(emergentReadersImg1, emergentReadersImg2, emergentReadersImg3, emergentReadersImg4);
    }
    if (selectedReaders['Independent Readers']) {
      filteredImages.push(independentReadersImg1, independentReadersImg2);
    }
    if (selectedReaders['Avid Readers']) {
      filteredImages.push(avidReadersImg1, avidReadersImg2);
    }

    // If no checkboxes are selected, return all images
    if (filteredImages.length === 0) {
      return [
        babiesAndToddlersImg1, babiesAndToddlersImg2,
        emergentReadersImg1, emergentReadersImg2, emergentReadersImg3, emergentReadersImg4,
        independentReadersImg1, independentReadersImg2,
        avidReadersImg1, avidReadersImg2
      ];
    }

    return filteredImages;
  };

  return (
    <div>
      <HomeHeader /> {/* Display HomeHeader */}
      
      <div className={styles.banner}></div> {/* Full width banner */}
      
      <div className={styles.mainContent}>
        <div className={styles.sidebar}>
          <h2>Home |</h2>

          <hr className={styles.divider} />

          {/* Refine By */}
          <h3>Refine By:</h3>
          <button className={styles.filterButton}>Emergent to Early Readers</button>

          <hr className={styles.divider} />
          
          {/* Price Section */}
          <div>
            <div onClick={() => setPriceOpen(!priceOpen)} className={styles.sectionTitle}>
              Price {priceOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {priceOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> From 0.00 to 799.99</label>
                <label><input type="checkbox" /> From 800.00 to 1,599.99</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Reader Section */}
          <div>
            <div onClick={() => setReaderOpen(!readerOpen)} className={styles.sectionTitle}>
              Reader {readerOpen ? <FaChevronDown /> : <FaChevronUp />}
            </div>
            {readerOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" checked={selectedReaders['Babies and Toddlers']} onChange={() => handleCheckboxChange('Babies and Toddlers')} /> Babies and Toddlers</label>
                <label><input type="checkbox" checked={selectedReaders['Emergent Readers']} onChange={() => handleCheckboxChange('Emergent Readers')} /> Emergent Readers</label>
                <label><input type="checkbox" checked={selectedReaders['Independent Readers']} onChange={() => handleCheckboxChange('Independent Readers')} /> Independent Readers</label>
                <label><input type="checkbox" checked={selectedReaders['Avid Readers']} onChange={() => handleCheckboxChange('Avid Readers')} /> Avid Readers</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Genre Section */}
          <div>
            <div onClick={() => setGenreOpen(!genreOpen)} className={styles.sectionTitle}>
              Book Genre {genreOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {genreOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> Holidays & Celebrations</label>
                <label><input type="checkbox" /> Fiction</label>
                <label><input type="checkbox" /> Emotions & Feelings</label>
                <label><input type="checkbox" /> Environmental Responsibility</label>
                <label><input type="checkbox" /> Bedtime Stories</label>
                <label><input type="checkbox" /> Special Educational Needs</label>
                <label><input type="checkbox" /> Family Life</label>
                <label><input type="checkbox" /> Religious Themes</label>
                <label><input type="checkbox" /> Historical Events</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Book Type Section */}
          <div>
            <div onClick={() => setTypeOpen(!typeOpen)} className={styles.sectionTitle}>
              Book Type {typeOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {typeOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> Picture Books</label>
                <label><input type="checkbox" /> Education Books</label>
                <label><input type="checkbox" /> Activity Books</label>
                <label><input type="checkbox" /> Interactive Books</label>
                <label><input type="checkbox" /> Children's Novel</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Book Format Section */}
          <div>
            <div onClick={() => setFormatOpen(!formatOpen)} className={styles.sectionTitle}>
              Book Format {formatOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {formatOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> Board Books</label>
                <label><input type="checkbox" /> Hardcover</label>
                <label><input type="checkbox" /> Paperback</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Language Section */}
          <div>
            <div onClick={() => setLanguageOpen(!languageOpen)} className={styles.sectionTitle}>
              Language {languageOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {languageOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> Filipino</label>
                <label><input type="checkbox" /> English</label>
              </div>
            )}
          </div>

          <hr className={styles.divider} />

          {/* Preloved Books Section */}
          <div>
            <div onClick={() => setPrelovedOpen(!prelovedOpen)} className={styles.sectionTitle}>
              Preloved Books {prelovedOpen ? <FaChevronDown /> : <FaChevronUp />} {/* Use icons */}
            </div>
            {prelovedOpen && (
              <div className={styles.checkboxGroup}>
                <label><input type="checkbox" /> Yes</label>
              </div>
            )}
          </div>
        </div>

        <div className={styles.content}>
          {/* Sort Section */}
          <div className={styles.sortSection}>
          <div className={styles.sortTitle}>
                  Sort By:
                </div>
                <div className={styles.dropdown} onClick={() => setSortOpen(!sortOpen)}>
                  <div className={styles.dropdownButton}>
                    {sortOption} {/* Display the selected option */}
                    <div className={styles.dropdownIcon}>
                      {sortOpen ? <FaChevronUp /> : <FaChevronDown />} {/* Toggle icon based on dropdown state */}
                    </div>
                  </div>

                  {sortOpen && (
                    <div className={styles.dropdownList}>
                      <div className={styles.dropdownItem} onClick={() => handleSortOptionChange("Price: Low to High")}>Price: Low to High</div>
                      <div className={styles.dropdownItem} onClick={() => handleSortOptionChange("Price: High to Low")}>Price: High to Low</div>
                      <div className={styles.dropdownItem} onClick={() => handleSortOptionChange("Newest Arrivals")}>Newest Arrivals</div>
                      <div className={styles.dropdownItem} onClick={() => handleSortOptionChange("Best Sellers")}>Best Sellers</div>
                    </div>
                  )}
                </div>
                <div className={styles.pagination}>
                  <span>1/1</span>
                  <span className={styles.pageButton}><FaChevronLeft /></span>
                  <span className={styles.pageButton}><FaChevronRight /></span>
                </div>
              </div>


            {/* Displaying filtered content based on selected categories */}
            <div className={styles.productGrid}>
            {loading ? (
              <p>Loading books...</p> // Show a loading message while data is being fetched
            ) : books.length === 0 ? (
              <p>No books available.</p> // Display message if no books are returned
            ) : (
              books.map((book, index) => (
                <div key={index} className={styles.productCard}>
                  <div className={styles.productBadge}>
                    <span className={styles.badgeBestSeller}>BEST SELLER</span>
                    <span className={styles.badgeSale}>SALE</span>
                  </div>
                  <img 
                    src={`http://localhost:8000/${book.image}`} 
                    alt={book.name} 
                    className={styles.productImage} 
                  />
                  <h3 className={styles.productTitle}>{book.name}</h3>
                  <p className={styles.productAuthor}>{book.author}</p>
                  <p className={styles.productPrice}>{book.price}</p>
                  <button className={styles.wishlistButton}></button>
                </div>
              ))
            )}
          </div>
      
        </div>
      </div>
    </div>
  );
};

export default Home;
