import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AiOutlineExclamationCircle } from 'react-icons/ai'; 
import styles from '../styles/login.module.css';
import '../styles/global.css';
import { jwtDecode } from 'jwt-decode';



const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({ email: '', password: '' });
  const navigate = useNavigate(); // Use useNavigate for navigation

  // Handle Google Sign-In
  useEffect(() => {
    /* global google */
    window.onload = () => {
      google.accounts.id.initialize({
        client_id: "49938045804-htn37ic66q8r4rkj0d96sikm9dogmjjb.apps.googleusercontent.com", // Use your Google client ID here
        callback: handleGoogleSignIn,
      });

      google.accounts.id.renderButton(
        document.getElementById("googleSignInButton"),
        { theme: "outline", size: "large" } // Customize button as needed
      );
    };
  }, []);

  // Handle Google sign-in and extract user info
  const handleGoogleSignIn = async (response) => {
    if (response.credential) {
      const googleUser = jwt_decode(response.credential); // Decode the JWT token to extract user info
      console.log("Google User Info:", googleUser);

      // You can now send the user's information to your backend for further processing (e.g., storing in database)
      const { email, sub, name } = googleUser; // You can extract more data if needed

      // Store the user's Google email and details in sessionStorage
      sessionStorage.setItem("email", email);
      sessionStorage.setItem("user", JSON.stringify({ name, email }));

      // Redirect to home page
      navigate("/home");
    } else {
      console.error("Google login failed");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let emailError = '';
    let passwordError = '';

    if (!email) emailError = 'Please enter your Childhood email or username.';
    if (!password) passwordError = 'Please enter your password.';

    setErrors({ email: emailError, password: passwordError });

    if (!emailError && !passwordError) {
      // Perform login logic here
      loginUser(email, password);
    }
  };

  const loginUser = async (email, password) => {
    try {
      const response = await fetch('http://localhost:8000/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({
          email: email,
          password: password,
        }),
      });

      const data = await response.json();

      if (data.success) {
        // Save the user details and email in sessionStorage
        sessionStorage.setItem('email', data.email);
        sessionStorage.setItem('user', JSON.stringify(data.user));
        // Redirect to a protected route
        navigate('/home');
      } else {
        // Show error message if login fails
        setErrors({ ...errors, password: data.error });
      }
    } catch (error) {
      console.error('Login failed', error);
      setErrors({ ...errors, email: 'An error occurred. Please try again.' });
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.logo}>
        <img src={require('../assets/main-logo.png')} alt="Childhood Logo" />
      </div>
      <h1 className={styles.title}>Log in to Childhood</h1>

      <div className={styles.authButtons}>
        <div id="googleSignInButton"></div> {/* This will render the Google Sign-In button */}
        <Link to="/bookPass">
          <button className={styles.bookPassButton}>
            <img src={require('../assets/icons/Password Book.png')} alt="Book Pass" /> Continue with Book Pass
          </button>
        </Link>
      </div>

      <div className={styles.divider}></div>

      <form className={styles.form} onSubmit={handleSubmit}>
        <div className={styles.inputGroup}>
          <h5>Email or username</h5>
          <input
            type="text"
            name="username"
            placeholder="Email or username"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={`${styles.input} ${errors.email ? styles.errorInput : ''}`}
          />
          {errors.email && (
            <p className={styles.errorMessage}>
              <AiOutlineExclamationCircle className={styles.errorIcon} /> {errors.email}
            </p>
          )}
        </div>

        <div className={styles.inputGroup}>
          <h5>Password</h5>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={`${styles.input} ${errors.password ? styles.errorInput : ''}`}
          />
          {errors.password && (
            <p className={styles.errorMessage}>
              <AiOutlineExclamationCircle className={styles.errorIcon} /> {errors.password}
            </p>
          )}
        </div>

        <button type="submit" className={styles.loginButton}>Log in</button>
      </form>

      <div className={styles.extraOptions}>
        <Link to="/forgotPass" className={styles.forgotPassword}>Forgot your password?</Link>
        <p>
          Donâ€™t have an account?{' '}
          <Link to="/signUp" className={styles.signUp}>Sign up for Childhood</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
