import React from 'react';
import '../styles/global.css';
import { FaHeart } from "react-icons/fa";

import mylittleDinosaur from '../assets/book-photos/babbies-toddlers/BEDTIME STORIES.png';

const MyWishlist = () => {
    const product = [
        {
            title: 'My Little Dinosaur (Hardcover)',
            price: '₱640.00',
            originalPrice: '₱800.00',
            discount: '-20%',
            label: 'Best Seller',
            image: mylittleDinosaur,
            saleType: 'Sale',
        },
    ]

    const styles = {
        productCardWrapper: {
            display: 'flex',
            justifyContent: 'center',
            width: '100%',
        },
        productCard: {
            margin: 0,
            flexShrink: 0,
            width: '300px',
            height: '500px',
            maxWidth: '300px',
            border: '1px solid #757575',
            padding: '45px 30px 20px 30px',
            textAlign: 'left',
            backgroundColor: '#fff',
            position: 'relative',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-evenly',
            boxSizing: 'border-box',
            transition: 'border 0.3s ease, box-shadow 0.3s ease',
        },
        labels: {
            position: 'absolute',
            fontFamiliy: 'Rethink Sans',
            top: '10px',
            left: '10px',
            display: 'flex',
            flexDirection: 'column',
            gap: '10px',
            zIndex: '2',
        },
        saleLabel: {
            backgroundColor: '#920000',
            textTransform: 'uppercase',
            textAlign: 'left',
            color: '#fff',
            fontFamiliy: 'Rethink Sans',
            fontSize: '1rem',
            fontWeight: 'bold',
            padding: '5px 0 5px 15px',
            width: '75px',
            marginLeft: '-10px', 
        },
        badgeLabel: {
            textTransform: 'uppercase',
            color: '#fff',
            fontFamiliy: 'Rethink Sans',
            fontSize: '1rem',
            fontWeight: 'bold',
            padding: '5px 20px 5px 15px',
            marginLeft: '-10px',
        },
        heartWrapper: {
            position: 'absolute',
            top: '20px',
            right: '150px',
            zIndex: '2',
            cursor: 'pointer',
        },
        wishlistIcon: {
            fontSize: '24px',
            color: '#920000',
        },
    };
  return (
    <div className={styles.productCardWrapper}>
        <div className={styles.productCard}>
            <div className={styles.labels}>
                <span className={styles.saleLabel}>{product.saleType}</span>
                <span className={styles.badgeLabel}
                    style={{
                        backgroundColor: product.label === 'Preloved' ? '#FF4F7D' : '#FC4902',
                    }}>
                    {product.label}</span>
                
                <div className={styles.heartWrapper}>
                    <FaHeart style={styles.wishlistIcon} />
                </div>
            </div>
            <img src={product.image} alt={product.title} className={styles.productImage}/>
            <div className={styles.productInfo}>
                <h3>{product.title}</h3>
                <p className={styles.productAuthor}>Kenpachi</p>
                <p className={styles.productPrice}>
                    <span className={styles.currentPrice}>{product.price}</span>
                    <span className={styles.originalPrice}>{product.originalPrice}</span>
                    <span className={styles.discount}>{product.discount}</span>
                </p>
            </div>
        </div>
    </div>
  );
};

export default MyWishlist;
