import React, { useState } from 'react';
import styles from '../styles/userChoices.module.css';
import '../styles/global.css';
import Header from '../components/header';

import { FaUserCircle } from "react-icons/fa";
import { BiSolidLocationPlus } from "react-icons/bi";

import MyAddresses from '../components/MyAddresses'; 
import Information from '../components/Information';
import MyOrder from '../components/MyOrder';
import MyWishlist from '../components/MyWishlist';
import MyVouchers from '../components/MyVouchers';

const UserChoices = () => {
  const [activeSection, setActiveSection] = useState(''); // Tracks which section is active

  const renderSection = () => {
    switch (activeSection) {
      case 'myAddresses':
        return <MyAddresses />;
      case 'information':
        return <Information />;
      case 'myOrder':
        return <MyOrder />;
      case 'myWishlist':
      default:
        return null;
    }
  };

  return (
    <div>
      <Header /> {/* Display Header */}
      <div className={styles.banner}>
        <div className={styles.navbar}>
          <button onClick={() => setActiveSection('information')} className={styles.navItem}>
          <FaUserCircle /> INFORMATION
          </button>
          <button onClick={() => setActiveSection('myAddresses')} className={styles.navItem}>
          <BiSolidLocationPlus />MY ADDRESSES
          </button>
          <button onClick={() => setActiveSection('myAddresses')} className={styles.navItem}>
          <BiSolidLocationPlus />MY ADDRESSES
          </button>
          <button onClick={() => setActiveSection('myAddresses')} className={styles.navItem}>
          <BiSolidLocationPlus />MY ADDRESSES
          </button>
          <button onClick={() => setActiveSection('myAddresses')} className={styles.navItem}>
          <BiSolidLocationPlus />MY ADDRESSES
          </button>
        </div>
      </div>
      <div className={styles.content}>
        {renderSection()} {/* Dynamically renders the selected section */}
      </div>
    </div>
  );
};

export default UserChoices;
