import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaRegHeart, FaHeart } from "react-icons/fa";
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';

import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import styles from '../styles/home.module.css';
import '../styles/global.css';
import HomeHeader from '../components/home-header';
import Footer from '../components/footer';
import B1 from '../assets/banners/1.png';
import B2 from '../assets/banners/2.png';
import B3 from '../assets/banners/3.png';

const Home = () => {
  const [hoveredHeart, setHoveredHeart] = useState(null);
  const [clickedHeart, setClickedHeart] = useState(null);
  const [books, setBooks] = useState([]); // State to hold books data
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch books from the getBook.php API
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://your-backend-url-path/getBook.php'); // Replace with your actual endpoint
        const data = await response.json();

        if (data.success) {
          setBooks(data.books); // Set the books data
        } else {
          setError(data.message);
        }
      } catch (err) {
        setError('Failed to fetch books. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  return (
    <div style={{ background: '#FFF' }}>
      <header style={{ position: 'fixed', width: '100%', zIndex: 1000, backgroundColor: 'white' }}>
        <HomeHeader />
      </header>

      <main style={{ paddingTop: '70px', marginBottom: '70px' }}>
        {/* Banner */}
        <div className={styles.banner}>
          <Swiper
            modules={[Navigation, Pagination, Autoplay]}
            navigation
            pagination={{ clickable: true }}
            autoplay={{ delay: 3000, disableOnInteraction: false }}
            loop
          >
            <SwiperSlide>
              <img src={B1} alt="Banner 1" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={B2} alt="Banner 2" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={B3} alt="Banner 3" />
            </SwiperSlide>
          </Swiper>
        </div>

        {/* Dynamic Books Section */}
        <div style={{ padding: '20px', marginTop: '1150px' }}>
          <h2 className={styles.title}>Christmas Super Sale</h2>
          {loading && <p>Loading books...</p>}
          {error && <p className={styles.error}>{error}</p>}
          {!loading && !error && books.length > 0 && (
            <div className={styles.productSwiperWrapper}>
              <Swiper
                modules={[Navigation]}
                navigation
                slidesPerView="auto"
                spaceBetween={10}
                centeredSlides={true}
                loop={true}
                className={styles.productSwiper}
              >
                {books.map((book, index) => (
                  <SwiperSlide key={book._id || index}>
                    <div className={styles.productCardWrapper}>
                      <div className={styles.productCard}>
                        <div className={styles.labels}>
                          <span className={styles.saleLabel}>Sale</span>
                          <span
                            className={styles.badgeLabel}
                            style={{
                              backgroundColor: book.label === 'Preloved' ? '#FF4F7D' : '#FC4902',
                            }}
                          >
                            {book.label || 'New'}
                          </span>

                          <div
                            className={styles.heartWrapper}
                            onMouseEnter={() => setHoveredHeart(index)}
                            onMouseLeave={() => setHoveredHeart(null)}
                            onClick={() => setClickedHeart(index)}
                          >
                            {clickedHeart === index || hoveredHeart === index ? (
                              <FaHeart className={styles.heartIconActive} />
                            ) : (
                              <FaRegHeart className={styles.heartIcon} />
                            )}
                          </div>
                        </div>
                        <img src={book.image || 'default-image.jpg'} alt={book.title} className={styles.productImage} />
                        <div className={styles.productInfo}>
                          <h3>{book.title}</h3>
                          <p className={styles.productAuthor}>{book.author || 'Unknown Author'}</p>
                          <p className={styles.productPrice}>
                            <span className={styles.currentPrice}>{book.price || 'â‚±0.00'}</span>
                            {book.originalPrice && (
                              <>
                                <span className={styles.originalPrice}>{book.originalPrice}</span>
                                <span className={styles.discount}>{book.discount || '-0%'}</span>
                              </>
                            )}
                          </p>
                        </div>
                      </div>
                      <div className={styles.addToBucket}>ADD TO BUCKET</div>
                    </div>
                  </SwiperSlide>
                ))}
              </Swiper>
            </div>
          )}
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default Home;
